import React, { useState, useEffect } from 'react';
import Field from './Field';
const Login = () => {
    const [email, setEmail] = useState('admin@gmail.com');
    const [password, setPassword] = useState('000');
    const [redirect, setRedirect] = useState(false);
    useEffect(() => {
        if (redirect) {
            setRedirect(false);
            window.location.href = '/home';
        }
    }, [redirect]);
    const token = sessionStorage.getItem("token");
                                                                    
    if (token) {
        window.location.href = "/Acceuil";
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        const data = new FormData();
        data.append('email', email);
        data.append('mdp', password);

        try {
            const response = await fetch('http://172.20.10.10:8080/utilisateurs/login', {
                headers: {
                    'Content-Type': 'application/json',
                },
                method: 'POST',
                body: data
            });
            const responseData = await response.json();
            console.log(responseData);

            setRedirect(true);
            /*if (response.ok) {
                sessionStorage.setItem('token', responseData.data);
                var token = sessionStorage.getItem('token');
                console.log(token);
                setRedirect(true);
            } else {
                console.log(responseData.message);
                alert(responseData.message);
            }*/
        } catch (error) {
            console.error('Erreur pendant la requête:', error);
            alert('Une erreur s\'est produite.');
        }
    };

    return (
        <div className="container mt-5">
            <h2 className="mb-4">Connexion</h2>
            <form onSubmit={handleSubmit}>
                <Field libelle="Email" type="email" onChange={(e) => setEmail(e.target.value)} value={email} />
                <Field libelle="Password" type="password" onChange={(e) => setPassword(e.target.value)} value={password} />
                <button type="submit" className="btn btn-primary">
                    Se connecter
                </button>
            </form>
        </div>
    );
};

export default Login;
