import axios from 'axios';
import React, { useEffect, useState } from 'react';
import Card from './Card';

const Countries = () => {
    const [data, setData] = useState([]);
    const [rangeValue, setRangeValue] = useState(36);
    const radios = ["Africa", "America", "Asia", "Europe", "Oceanie"];
    const [selectedRadios, setSelectedRadios] = useState("");

    // Le useEffect se joue lorsque le composant est monté
    useEffect(() => {
        axios.get("https://restcountries.com/v3.1/all")
            .then((res) => setData(res.data));
    }, [])

    return (
        <div className='countries'>
            <ul className="radio-container">
                <input type="range" min="1" max="250"
                    defaultValue={rangeValue}
                    onChange={(e) => setRangeValue(e.target.value)}
                />
                {radios.map((continent) => (
                    <li>
                        <input type="radio" name=""
                            id={continent}
                            name="continent"
                            checked={continent === selectedRadios}
                            onChange={(e) => setSelectedRadios(e.target.id)}
                        />
                        <label htmlFor={continent}>{continent}</label>
                    </li>
                ))}

            </ul>
            {selectedRadios && (
                <button onClick={() => setSelectedRadios("")}>Annuler la recherche</button>
            )}
            <ul>
                {
                    data
                        .filter((country) => country.continents[0].includes(selectedRadios))
                        .sort((a, b) => b.population - a.population)
                        .slice(0, rangeValue)
                        .map((country, index) =>
                            <Card key={index} country={country} />
                        )
                }
            </ul>
        </div >
    );
};

export default Countries;
