import React, { useEffect } from 'react';

const Logout = () => {
    const handleLogout = async () => {
        // Supprimer le token du localStorage
        localStorage.removeItem('token');

        try {
            // Effectuer une requête au point de déconnexion
            const response = await fetch('https://e-vehicule-production-c2e4.up.railway.app/utilisateurs/logout', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                // Vous pouvez ou non inclure le corps de la requête, selon les exigences de votre API
            });

            if (response.ok) {
                // Réussite de la déconnexion
                console.log('Déconnexion réussie');
            } else {
                // Gestion des erreurs de déconnexion
                const responseData = await response.json();
                console.log(responseData.message);
                alert("Erreur lors de la déconnexion : " + responseData.message);
            }
        } catch (error) {
            // Gestion des erreurs liées à la requête
            console.error('Erreur pendant la requête:', error);
            alert('Une erreur s\'est produite.');
        }

        // Rediriger vers la page de connexion
        window.location.href = '/login';
    }

    useEffect(() => {
        // Appeler la fonction de déconnexion lors du montage du composant
        handleLogout();
    }, []);

    return null;
};

export default Logout;
