import React, { useState, useEffect } from 'react';
import Field from './Field';
const Login = () => {
    const [email, setEmail] = useState('haihsinjo@example.com');
    const [password, setPassword] = useState('hasinjo');
    const [redirect, setRedirect] = useState(false);
    useEffect(() => {
        if (redirect) {
            setRedirect(false);
            window.location.href = '/home';
        }
    }, [redirect]);
    const token = localStorage.getItem("token");

    if (token) {
        window.location.href = "/";
    }

    const handleSubmit = async (e) => {
        e.preventDefault();

        const data = {
            email: email,
            mdp: password
        };

        try {
            const response = await axios.post('http://localhost:8080/api/auth/login', data, {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json',
                    // Add the Authorization header here if necessary
                },
            });

            if (response.status === 200) {
                localStorage.setItem('token', response.data.data);
                var token = localStorage.getItem('token');
                console.log(token);
                setRedirect(true);
            } else {
                console.log(response.data.message);
                alert(response.data.message);
            }
        } catch (error) {
            console.error('Error during the request:', error);
            alert('An error occurred.');
        }
    };

    return (
        <div className="container mt-5">
            <h2 className="mb-4">Connexion</h2>
            <form onSubmit={handleSubmit}>
                <Field libelle="Email" type="email" onChange={(e) => setEmail(e.target.value)} value={email} />
                <Field libelle="Password" type="password" onChange={(e) => setPassword(e.target.value)} value={password} />
                <button type="submit" className="btn btn-primary">
                    Se connecter
                </button>
            </form>
        </div>
    );
};

export default Login;
