import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import LogoSidebar from './logo/LogoSidebar';

const Sidebar = () => {
    const [isMenuOpen, setMenuOpen] = useState(false);
    const data = [
        { to: '/tables/data', text: 'DataTables' },
        { to: '/tables/jsgrid', text: 'jsGrid' },
    ];
    const [dataTable, setDataTable] = useState(data);
    useEffect(() => {
        // Get the HTML element by reference
        const element = sparklineElementRef.current;

        // Instantiate the Sparkline object
        const sparkline = new Sparkline(element, {
            // Your Sparkline options here
            lineColor: "blue",
            // ... other options ...
        });

        // Draw the sparkline with the provided data
        sparkline.draw(data);

        // Optionally, return a cleanup function if needed
        return () => {
            // Perform any cleanup here if necessary
        };
    }, []); const toggleMenu = () => {
        setMenuOpen(!isMenuOpen);
    };


    return (
        <>
            <aside className="main-sidebar sidebar-dark-primary elevation-4">

                <LogoSidebar />
                <br />
                {/* Sidebar Menu */}
                <div className="sidebar">
                    <ul className="nav nav-pills nav-sidebar flex-column" data-widget="treeview" role="menu" data-accordion="false">
                        <li className={`nav-item has-treeview ${isMenuOpen ? 'menu-open' : ''}`}>
                            <a href="#" onClick={toggleMenu} className="nav-link">
                                <i className="nav-icon fas fa-table"></i>
                                <p>
                                    Tables
                                    <i className="fas fa-angle-left right"></i>
                                </p>
                            </a>
                            <ul className="nav nav-treeview">
                                {dataTable.map((item, index) => (
                                    <li key={index} className="nav-item">
                                        <Link to={item.to} className="nav-link">
                                            <i className="far fa-circle nav-icon"></i>
                                            <p>{item.text}</p>
                                        </Link>
                                    </li>
                                ))}
                            </ul>
                        </li>
                    </ul>
                </div>
                {/* /.sidebar-menu */}
            </aside>
        </>
    );
};

export default Sidebar;
