import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
    const [marqueData, setMarqueData] = useState([]);
    const token = localStorage.getItem('token');

    console.log(token);
    useEffect(() => {
        if (!token) {
            window.location.href = '/login';
        }
        const fetchData = async () => {
            try {
                const headers = new Headers();
                headers.append('token', token);
                const response = await fetch('http://localhost:8080/api/v1/marques', {
                    method: 'GET',
                    headers: headers,
                });
                const datas = await response.json();
                if (response.ok) {
                    setMarqueData(datas.data);
                } else {
                    alert(datas.message);
                    window.location.href = '/logout';
                }

                console.log(datas);
            } catch (error) {
                console.error('Erreur lors de la récupération des données:', error);
            }
        };

        fetchData();
    }, [token]);


    return (
        <div className="container mt-4">
            {marqueData.length > 0 ? (
                <div>
                    {marqueData.map((marque) => (
                        <div key={marque.id_marque} className="card mb-3">
                            <div className="card-body">
                                <h5 className="card-title">ID Marque: {marque.id_marque}</h5>
                                <p className="card-text">Marque: {marque.marque}</p>
                            </div>
                        </div>
                    ))}
                </div>
            ) : (
                <p>Chargement...</p>
            )}

            <Link to="/logout" >
                Déconnexion
            </Link>
        </div>
    );
};

export default Home;
