import React, { useState, useEffect } from 'react';

const Annonce = () => {
    const [annonceData, setAnnonceData] = useState([]);
    const token = sessionStorage.getItem('token');

    useEffect(() => {
        const fetchData = async () => {
            try {
                if (!token) {
                    window.location.href = '/login';
                }

                const response = await fetch('https://webservice-production-4a2c.up.railway.app/annonces/nonvalide', {
                    method: 'GET',
                    headers: {
                        'Authorization': `Bearer ${token}`,
                        'Content-Type': 'application/json',
                    },
                });

                const data = await response.json();

                if (response.ok) {
                    setAnnonceData(data.data);
                } else {
                    alert(JSON.stringify(data));
                    window.location.href = '/logout';
                }
            } catch (error) {
                console.error('Erreur lors de la récupération des données:', error);
            }
        };

        fetchData();
    }, [token]);

    const handleValidation = (annonceId) => {
        // Vous pouvez implémenter ici la logique pour valider l'annonce avec l'ID donné
        console.log(`Annonce validée avec l'ID: ${annonceId}`);
    };

    return (
        <div className="container mt-4">
            {annonceData.length > 0 ? (
                <div>
                    {annonceData.map((annonce) => (
                        <div key={annonce.id} className="card mb-3">
                            <div className="card-body">
                                <h5 className="card-title">ID Annonce: {annonce.id}</h5>
                                <p className="card-text">Titre: {annonce.titre}</p>
                                <p className="card-text">Description: {annonce.description}</p>
                               
                                <button
                                    className="btn btn-primary"
                                    onClick={() => handleValidation(annonce.id)}
                                >
                                    Valider
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            ) : (
                <p>Chargement...</p>
            )}
        </div>
    );
};

export default Annonce;
