import React from 'react';
import "../../assets/styles/stat.css";

const FrameStatistique = () => {
    const [statData, setStatData] = useState([]);
    const token = sessionStorage.getItem('token');

    console.log(token);
    useEffect(() => {
        if (!token) {
            window.location.href = '/login';
        }
        const fetchData = async () => {
            try {
                const headers = new Headers();
                headers.append('token', token);
                const response = await fetch('https://e-vehicule-production-c2e4.up.railway.app/annonces/statistique', {
                    method: 'GET',
                    headers: {
                        'Authorization':`Bearer ${token}`,
                        'Content-Type':'application/json',
                    },
                });
                const datas = await response.json();
                if (response.ok) {
                    setData(datas.data);
                } else {
                    alert(JSON.stringify(datas));
                    window.location.href = '/logout';
                }

                console.log(datas);
            } catch (error) {
                console.error('Erreur lors de la récupération des données:', error);
            }
        };

        fetchData();
    }, [token]);

    return (
        <div className="simple-bar-chart">
            {statData.map((categorie) => (
                <div className="item" key={index}>
                    <div className="label">{categorie.nom}</div>
                    <div className="value">{item.effectif}%</div>
                </div>
            ))}
        </div>
    );
};

export default FrameStatistique;
