import React, { useState } from 'react';
import '../assets/styles/Home.css'
import Price_stock from './Price_stock';
import Bouttontocard from './Bouttontocard';
import Enlargedimage from './Produit_body/Enlargedimage';
const Produit = () => {
    const [imageAgrandie, setImageAgrandie] = useState('./logo192.png');
    const images = [
        './logo192.png',
        './p.jpg',
        './b.jpg',
    ];
    const style = {
        marginLeft: '10%'
    };

    const handleClick = (url) => {
        setImageAgrandie(url);
    };

    return (
        <>
            <div className='col-md-3 produit'>
                <Enlargedimage imageAgrandie={imageAgrandie} />
                <div className="row">
                    <div className="offset-md-1"></div>
                    {
                        images.map((url, index) => (
                            <div style={style} className="col-md-2 offset-md-1">
                                <img key={index} src={url} width="47" height="38" alt={`Image ${index + 1}`} onClick={() => handleClick(url)} />
                            </div>
                        ))}
                </div> <br />
                <Price_stock price={1000} stock={9} />
                <Bouttontocard />
            </div>
        </>
    );
};

export default Produit;
