import React, { useState, useEffect } from 'react';
import Field from './Field';

const Login = () => {
    const [email, setEmail] = useState('haisinjo@example.com');
    const [password, setPassword] = useState('hasinjo');
    const [redirect, setRedirect] = useState(false);

    useEffect(() => {
        if (redirect) {
            setRedirect(false);
            window.location.href = '/';
        }
    }, [redirect]);


    const handleSubmit = async (e) => {
        e.preventDefault();

        console.log('Email:', email);
        console.log('Password:', password);
        const data = new FormData();
        data.append('email', email);
        data.append('mdp', password);


        const response = await fetch('http://localhost:808/api/auth/login', {
            method: 'POST',
            body: data
        });
        if (response.ok) {
            //traitement de la réponse OK
            const data = await response.json();
            console.log(data.datas);
            alert(data.message);
            localStorage.setItem('token', data.datas);
            setRedirect(true);
        }
        else {
            //traitement de la réponse KO
            console.log("failed");
            alert("erreur");
        }

    };

    return (
        <div className="container mt-5">
            <h2 className="mb-4">Connexion</h2>
            <form onSubmit={handleSubmit}>
                <Field libelle="Email" type="email" onChange={(e) => { setEmail(e.target.value) }} value={email} />
                <Field libelle="Password" type="password" onChange={(e) => { setPassword(e.target.value) }} value={password} />
                <button type="submit" className="btn btn-primary">
                    Se connecter
                </button>
            </form>
        </div>
    );
};

export default Login;
