import '../assets/styles/Home.css'
import Price_stock from './Price_stock';
import Bouttontocard from './Bouttontocard';
const Produit = () => {

    const images = [
        'url_image_1.jpg',
        'url_image_2.jpg',
        'url_image_3.jpg',
        // Ajoutez autant d'URLs d'images que nécessaire
    ];



    return (
        <>
            <div className='col-md-3 produit'>
                <div className="row">
                    <div className="col-md-12 imageagrandie">
                        <img src="./logo192.png" width="237" height="202" alt="" />
                    </div>
                </div> <br />
                <hr />
                <div className="row">
                    <div className="col-md-2"></div>
                    <div className="col-md-2 sousimg">
                        <img src="./p.jpg" width="47" height="38" alt="" />
                    </div>
                    <div className="col-md-1"></div>
                    <div className="col-md-2 sousimg">
                        <img src="./p.jpg" width="47" height="38" alt="" />
                    </div>
                    <div className="col-md-1"></div>
                    <div className="col-md-2 sousimg">
                        <img src="./logo192.png" width="47" height="38" alt="" />
                    </div>
                </div> <br />
                <Price_stock price={1000} stock={9} />
                <Bouttontocard />


            </div>
        </>
    );
};

export default Produit;
