import React, { useState, useEffect } from 'react';
import Field from './Field';
const Login = () => {
    const [email, setEmail] = useState('haihsinjo@example.com');
    const [password, setPassword] = useState('hasinjo');
    const [redirect, setRedirect] = useState(false);
    useEffect(() => {
        if (redirect) {
            setRedirect(false);
            window.location.href = '/home';
        }
    }, [redirect]);
    const token = localStorage.getItem("token");

    if (token) {
        window.location.href = "/";
    }

    const handleSubmit = async (e) => {
        e.preventDefault();

        const data = {
            email: email,
            mdp: password
        };

        try {
            const response = await fetch('http://localhost:8080/api/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json',
                    // Ajoutez l'en-tête Authorization ici si nécessaire
                },
                body: JSON.stringify(data),
            });

            const responseData = await response.json();

            if (response.ok) {
                localStorage.setItem('token', responseData.data);
                var token = localStorage.getItem('token');
                console.log(token);
                setRedirect(true);
            } else {
                console.log(responseData.message);
                alert(responseData.message);
            }
        } catch (error) {
            console.error('Erreur pendant la requête:', error);
            alert('Une erreur s\'est produite.');
        }
    };

    return (
        <div className="container mt-5">
            <h2 className="mb-4">Connexion</h2>
            <form onSubmit={handleSubmit}>
                <Field libelle="Email" type="email" onChange={(e) => setEmail(e.target.value)} value={email} />
                <Field libelle="Password" type="password" onChange={(e) => setPassword(e.target.value)} value={password} />
                <button type="submit" className="btn btn-primary">
                    Se connecter
                </button>
            </form>
        </div>
    );
};

export default Login;
